function eldob(_OBJ,_PLA,_PARAM){
	list = _OBJ.getAllPerceptibility();
	evn = _OBJ.createSimpleSourceEvent(list, _PLA, "%S egy elegáns mozdulattal eldobja az almacsutkáját.", null, "Egy elegáns mozdulattal megszabadulsz az almacsutkádtól.");
	if(evn!=null){
		evn.fire();
	}
	_OBJ.destr();
	return true;
}
